import React from "react";
import styled from "styled-components";
import { Tablet, Desktop } from "../Responsive";

const Wrapper = styled.div`
  margin: 0;
  padding: 0;
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  color: rgba(52, 52, 52, 1);
  background-color: rgba(16, 13, 177, 1);
  place-items: center;
  overflow: hidden;
  font-family: "Poppins", sans-serif;

  .register {
    background: #ffffff;
    border: 1px solid #e7e7e7;
    border-radius: 15px;
    position: relative;
    width: 80%;
    height: 500px;
    padding: 20px 30px;
    ${Tablet({ width: "50%" })}
    ${Desktop({ width: "30%" })}
  }

  .brand-logo {
    height: 70px;
    width: 50px;
    margin: auto;

    display: flex;
    justify-content: center;
    align-items: center;
  }
  .brand-logo img {
    height: 100%;
    width: 100%;
  }
  h3 {
    font-size: 20px;
    text-align: center;
  }

  .inputs {
    text-align: left;
    margin-top: 15px;
  }

  label,
  input,
  button {
    display: block;
    width: 100%;
    padding: 0;
    border: none;
    outline: none;
    box-sizing: border-box;
  }

  label {
    margin-bottom: 2px;
    font-size: 12px;
  }

  label:nth-of-type(2) {
    margin-top: 12px;
  }

  input::placeholder {
    color: rgba(52, 52, 52, 1);
  }

  input {
    background: #ecf0f3;
    padding: 10px;
    padding-left: 20px;
    height: 40px;
    font-size: 12px;
    border-radius: 50px;
    box-shadow: inset 6px 6px 6px #cbced1, inset -6px -6px 6px white;
  }

  button {
    color: white;
    margin-top: 20px;
    background: #1da1f2;
    height: 30px;
    border-radius: 20px;
    cursor: pointer;
    font-weight: 900;
    box-shadow: 6px 6px 6px #cbced1, -6px -6px 6px white;
    transition: 0.5s;
  }

  button:hover {
    box-shadow: none;
  }

  a {
    position: absolute;
    font-size: 15px;
    text-decoration: none;
    color: #1da1f2;
    border-radius: 10px;
    /* padding: 2px; */
    margin-left: 10px;
  }
`;

const LoginPage = () => {
  return (
    <Wrapper>
      <div className="register">
        <div className="brand-logo">
          <img
            src="/images/brand.png"
            alt="brand"
            className="d-inline-block align-text-top me-1"
          />
        </div>

        <h3 className="mt-2">Login</h3>

        <form action="/auth/login" method="post" className="input">
          <div className="mb-3">
            <label className="form-label">Email</label>
            <input
              type="email"
              className="form-control"
              name="email"
              placeholder="Olaboluwatofezzy@ymail.com"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              name="password"
              placeholder="XXXXXXXXXXXXXX"
            />
          </div>
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </form>
        <div className="mt-3">
          <p className="">
            Not yet a Member? <a href="/auth/register">Sign Up</a>
          </p>
        </div>
      </div>
    </Wrapper>
  );
};

export default LoginPage;
